// eventParams.js
const EventEmitter = import('events');
const event = new EventEmitter();

// Listener with parameters
EventEmitter.on('greetUser', (name, age) => {
  console.log(`Hello, ${name}, you are ${age} years old!`);
});

// Emit event with parameters
EventEmitter.emit('greetUser', 'Monika', 25);
